package com.sdp.swiftwallet.presentation.main.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.floatingactionbutton.FloatingActionButton;
import com.sdp.cryptowalletapp.R;
import com.sdp.swiftwallet.common.FirebaseUtil;
import com.sdp.swiftwallet.domain.model.Contact;
import com.sdp.swiftwallet.domain.model.ContactAdapter;

import java.util.ArrayList;
import java.util.List;

/**
 * A simple {@link Fragment} subclass.
 * create an instance of this fragment.
 */
public class MessageFragment extends Fragment {
    private List<Contact> contacts = new ArrayList<>();
    private RecyclerView contactRecyclerView;

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_message, container, false);

        contactRecyclerView = view.findViewById(R.id.contactsRecyclerView);
        ContactAdapter contactAdapter = new ContactAdapter(contacts);
        contactRecyclerView.setAdapter(contactAdapter);
        contactRecyclerView.setVisibility(View.VISIBLE);

        FloatingActionButton addContactsBtn = view.findViewById(R.id.addContactBtn);
        addContactsBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                addContact();
                contactAdapter.notifyItemInserted(0);
            }
        });

        return view;
    }

    private void addContact() {
        Contact contact = new Contact();
        contact.name = "John Doe";
        contact.email = "john.doe@random.com";
        contact.image = "";
        contact.token = "12";
        contacts.add(contact);
    }
}